contracts/IPatentRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IPatentRegistry {
    function getPatent(uint256 Pid) external view returns (
        string memory title,
        string memory ipfsHash,
        uint256 price,
        bool isForSale,
        address inventor,
        uint256 createdAt,
        string memory patentId,
        bool isVerified
    );
}

contract PatentRegistry is IPatentRegistry {
    // Custom Owner implementation
    address private _owner;
    bool private _paused;

    struct Patent {
        string title;
        string ipfsHash;
        uint256 price;
        bool isForSale;
        address inventor;
        uint256 createdAt;
        string patentId;
        bool isVerified;
    }

    uint256 private nextPatentId = 1;
    mapping(uint256 => Patent) private _patents;
    mapping(string => bool) private _patentIdExists;
    mapping(address => uint256[]) private _inventorPatents;

    // Events
    event PatentRegistered(uint256 indexed patentId, string title, address inventor);
    event PatentVerified(uint256 indexed patentId);
    event PatentUpdated(uint256 indexed patentId, string ipfsHash);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event Paused(address account);
    event Unpaused(address account);

    // Custom modifiers
    modifier onlyOwner() {
        require(msg.sender == _owner, "Caller is not the owner");
        _;
    }

    modifier whenNotPaused() {
        require(!_paused, "Contract is paused");
        _;
    }

    modifier whenPaused() {
        require(_paused, "Contract is not paused");
        _;
    }

    constructor() {
        _owner = msg.sender;
        _paused = false;
    }

    // Owner management functions
    function owner() public view returns (address) {
        return _owner;
    }

    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner is the zero address");
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    // Pausable functions
    function paused() public view returns (bool) {
        return _paused;
    }

    function pause() public onlyOwner whenNotPaused {
        _paused = true;
        emit Paused(msg.sender);
    }

    function unpause() public onlyOwner whenPaused {
        _paused = false;
        emit Unpaused(msg.sender);
    }

    // Patent management functions
    function registerPatent(
        string memory title,
        string memory ipfsHash,
        string memory patentId
    ) external whenNotPaused returns (uint256) {
        require(!_patentIdExists[patentId], "Patent ID already exists");
        require(bytes(title).length > 0, "Title cannot be empty");
        require(bytes(ipfsHash).length > 0, "IPFS hash cannot be empty");

        uint256 newPatentId = nextPatentId;
        nextPatentId++;

        _patents[newPatentId] = Patent({
            title: title,
            ipfsHash: ipfsHash,
            price: 0,
            isForSale: false,
            inventor: msg.sender,
            createdAt: block.timestamp,
            patentId: patentId,
            isVerified: false
        });

        _patentIdExists[patentId] = true;
        _inventorPatents[msg.sender].push(newPatentId);

        emit PatentRegistered(newPatentId, title, msg.sender);
        return newPatentId;
    }

    function verifyPatent(uint256 patentId) external onlyOwner whenNotPaused {
        require(_patents[patentId].inventor != address(0), "Patent does not exist");
        require(!_patents[patentId].isVerified, "Patent already verified");

        _patents[patentId].isVerified = true;
        emit PatentVerified(patentId);
    }

    function updatePatentMetadata(
        uint256 patentId,
        string memory newIpfsHash
    ) external whenNotPaused {
        require(_patents[patentId].inventor == msg.sender, "Not the patent owner");
        require(bytes(newIpfsHash).length > 0, "IPFS hash cannot be empty");

        _patents[patentId].ipfsHash = newIpfsHash;
        emit PatentUpdated(patentId, newIpfsHash);
    }

    function setPatentPrice(uint256 patentId, uint256 newPrice) external whenNotPaused {
        require(_patents[patentId].inventor == msg.sender, "Not the patent owner");
        require(_patents[patentId].isVerified, "Patent not verified");

        _patents[patentId].price = newPrice;
        _patents[patentId].isForSale = newPrice > 0;
    }

    function getPatent(uint256 patentId) external view override returns (
        string memory title,
        string memory ipfsHash,
        uint256 price,
        bool isForSale,
        address inventor,
        uint256 createdAt,
        string memory patentIdStr,
        bool isVerified
    ) {
        Patent storage patent = _patents[patentId];
        require(patent.inventor != address(0), "Patent does not exist");

        return (
            patent.title,
            patent.ipfsHash,
            patent.price,
            patent.isForSale,
            patent.inventor,
            patent.createdAt,
            patent.patentId,
            patent.isVerified
        );
    }

    function getInventorPatents(address inventor) external view returns (uint256[] memory) {
        return _inventorPatents[inventor];
    }

    function patentExists(string memory patentId) external view returns (bool) {
        return _patentIdExists[patentId];
    }

    function getTotalPatents() external view returns (uint256) {
        return nextPatentId - 1;
    }
}

contracts/PatentFactory.sol.content:
// PatentFactory.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./PatentToken.sol";

contract PatentFactory is Ownable {
    address public patentRegistry;
    mapping(uint256 => address) public patentTokens;
    mapping(address => bool) public isPatentToken;

    event PatentTokenCreated(uint256 indexed patentId, address tokenAddress);

    constructor(address _patentRegistry) Ownable(msg.sender) {
        patentRegistry = _patentRegistry;
    }

    function createPatentToken(
        string memory name,
        string memory symbol,
        uint256 patentId
    ) external payable returns (address) {
        require(patentTokens[patentId] == address(0), "Token already exists for patent");

        PatentToken newToken = new PatentToken{value: msg.value}(
            name,
            symbol,
            patentRegistry,
            patentId,
            msg.sender
        );

        address tokenAddress = address(newToken);
        patentTokens[patentId] = tokenAddress;
        isPatentToken[tokenAddress] = true;

        emit PatentTokenCreated(patentId, tokenAddress);
        return tokenAddress;
    }

    function getPatentToken(uint256 patentId) external view returns (address) {
        return patentTokens[patentId];
    }

    function updatePatentRegistry(address newRegistry) external onlyOwner {
        patentRegistry = newRegistry;
    }
}

contracts/PatentToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

interface IPatentRegistry {
    function getPatent(uint256 Pid) external view returns (
        string memory title,
        string memory ipfsHash,
        uint256 price,
        bool isForSale,
        address inventor,
        uint256 createdAt,
        string memory patentId,
        bool isVerified
    );
}

contract PatentToken is ERC20, Ownable, ReentrancyGuard {
    using Math for uint256;

    // Constants
    uint256 public constant MAX_SUPPLY = 1_000_000 * 10**18;
    uint256 public constant MIN_PRICE = 1e10;
    uint256 public constant MIN_LIQUIDITY = 0.1 ether;

    // Patent specific details
    struct Patent {
        string title;
        string ipfsHash;
        uint256 price;
        bool isForSale;
        address inventor;
        uint256 createdAt;
        string patentId;
        bool isVerified;
    }

    // State variables
    uint256 public basePrice;
    uint256 public currentPrice;
    address public immutable patentRegistryAddress;
    uint256 public immutable patentId;
    Patent public patentDetails;
    uint256 public liquidityPool;
    uint256 public volume24h;
    uint256 public lastPriceUpdate;
    uint256 public holdersCount;
    bool public isTrading;

    // Trading history
    struct TradeMetrics {
        uint256 timestamp;
        uint256 price;
        uint256 volume;
    }

    TradeMetrics[] public tradeHistory;
    mapping(address => uint256) public userTrades;

    // Events
    event TokensPurchased(address indexed buyer, uint256 amount, uint256 price);
    event TokensSold(address indexed seller, uint256 amount, uint256 price);
    event PriceUpdated(uint256 oldPrice, uint256 newPrice, uint256 timestamp);
    event LiquidityAdded(address indexed provider, uint256 amount);
    event LiquidityRemoved(address indexed provider, uint256 amount);
    event PatentDetailsUpdated(string ipfsHash, uint256 price);

    constructor(
        string memory name,
        string memory symbol,
        address _patentRegistryAddress,
        uint256 _patentId,
        address initialOwner
    ) payable ERC20(name, symbol) Ownable(initialOwner) {
        require(msg.value >= MIN_LIQUIDITY, "Insufficient initial liquidity");

        basePrice = MIN_PRICE;
        currentPrice = basePrice;
        patentRegistryAddress = _patentRegistryAddress;
        patentId = _patentId;
        isTrading = true;
        lastPriceUpdate = block.timestamp;

        // Initialize liquidity and mint tokens
        liquidityPool = msg.value;
        _mint(address(this), MAX_SUPPLY / 2);
        _mint(initialOwner, MAX_SUPPLY / 2);

        // Fetch initial patent details
        IPatentRegistry registry = IPatentRegistry(_patentRegistryAddress);
        (
            string memory title,
            string memory ipfsHash,
            uint256 price,
            bool isForSale,
            address inventor,
            uint256 createdAt,
            string memory patentIdStr,
            bool isVerified
        ) = registry.getPatent(_patentId);

        patentDetails = Patent({
            title: title,
            ipfsHash: ipfsHash,
            price: price,
            isForSale: isForSale,
            inventor: inventor,
            createdAt: createdAt,
            patentId: patentIdStr,
            isVerified: isVerified
        });

        emit LiquidityAdded(msg.sender, msg.value);
    }

    function calculatePrice(uint256 amount, bool isBuy) public view returns (uint256) {
        require(amount > 0, "Amount must be greater than 0");
        uint256 reserve = balanceOf(address(this));
        uint256 scalingFactor = 1e18;
        uint256 priceIncrease = (amount * scalingFactor) / (reserve + 1);
        uint256 finalPrice = MIN_PRICE + priceIncrease;
        return finalPrice;
    }

    function buyTokens(uint256 amount) external payable nonReentrant {
        require(isTrading, "Trading is paused");
        require(amount > 0, "Amount must be greater than 0");
        require(patentDetails.isForSale, "Patent not for sale");

        uint256 liquidityTokens = balanceOf(address(this));
        require(amount <= liquidityTokens, "Insufficient liquidity");

        uint256 price = calculatePrice(amount, true);
        uint256 totalCost = (amount * price) / 10**18;
        require(msg.value >= totalCost, "Insufficient payment");

        _transfer(address(this), msg.sender, amount);

        // Update trading metrics
        tradeHistory.push(TradeMetrics({
            timestamp: block.timestamp,
            price: price,
            volume: amount
        }));

        currentPrice = price;
        lastPriceUpdate = block.timestamp;
        volume24h += amount;

        if (balanceOf(msg.sender) == amount) {
            holdersCount++;
        }

        // Handle liquidity
        liquidityPool += totalCost;

        if (msg.value > totalCost) {
            payable(msg.sender).transfer(msg.value - totalCost);
        }

        emit TokensPurchased(msg.sender, amount, price);
        emit PriceUpdated(currentPrice, price, block.timestamp);
    }

    function sellTokens(uint256 amount) external nonReentrant {
        require(isTrading, "Trading is paused");
        require(amount > 0, "Amount must be greater than 0");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        require(liquidityPool > 0, "No liquidity");

        uint256 price = calculatePrice(amount, false);
        uint256 saleProceeds = (amount * price) / 10**18;
        require(liquidityPool >= saleProceeds, "Insufficient liquidity for sale");

        _transfer(msg.sender, address(this), amount);

        currentPrice = price;
        lastPriceUpdate = block.timestamp;
        volume24h += amount;

        if (balanceOf(msg.sender) == 0) {
            holdersCount--;
        }

        tradeHistory.push(TradeMetrics({
            timestamp: block.timestamp,
            price: price,
            volume: amount
        }));

        liquidityPool -= saleProceeds;
        payable(msg.sender).transfer(saleProceeds);

        emit TokensSold(msg.sender, amount, price);
        emit PriceUpdated(currentPrice, price, block.timestamp);
    }

    // Admin functions
    function updatePatentDetails(string memory newIpfsHash, uint256 newPrice) external onlyOwner {
        patentDetails.ipfsHash = newIpfsHash;
        patentDetails.price = newPrice;
        emit PatentDetailsUpdated(newIpfsHash, newPrice);
    }

    function setPatentForSale(bool forSale) external onlyOwner {
        patentDetails.isForSale = forSale;
    }

    function withdrawExcessLiquidity(uint256 amount) external onlyOwner {
        require(liquidityPool > MIN_LIQUIDITY + amount, "Insufficient excess liquidity");
        liquidityPool -= amount;
        payable(owner()).transfer(amount);
    }

    // View functions
    function getPatentDetails() external view returns (Patent memory) {
        return patentDetails;
    }

    function getTradeHistory(uint256 count) external view returns (TradeMetrics[] memory) {
        uint256 historyLength = tradeHistory.length;
        uint256 resultCount = Math.min(count, historyLength);
        TradeMetrics[] memory result = new TradeMetrics[](resultCount);

        for (uint256 i = 0; i < resultCount; i++) {
            result[i] = tradeHistory[historyLength - resultCount + i];
        }

        return result;
    }

    function getTokenMetrics() external view returns (
        uint256 _currentPrice,
        uint256 _basePrice,
        uint256 _liquidityPool,
        uint256 _volume24h,
        uint256 _holdersCount,
        bool _isTrading
    ) {
        return (
            currentPrice,
            basePrice,
            liquidityPool,
            volume24h,
            holdersCount,
            isTrading
        );
    }

    receive() external payable {}
    fallback() external payable {}
}

